<script>
    import { onMount } from "svelte";
    import { Button, Dropdown } from "flowbite-svelte";
    import toast, { Toaster } from "svelte-french-toast";
    import hljs from "highlight.js";
    import "highlight.js/styles/atom-one-dark.css";
    import Highlight from "svelte-highlight";
    import typescript from "svelte-highlight/languages/css";
  
    let imageBase64 = `data:image/png;base64,`;
    let bgimage;
    $: bgimage = "url(" + imageBase64 + ")";
    $: console.log("this ", bgimage);
  
    async function handleFileUpload(event) {
      const file = event.target.files[0];
      if (!file) return;
  
      // Read the file as base64
      const reader = new FileReader();
      reader.onload = () => {
        const img = new Image();
        img.src = reader.result;
        img.onload = () => {
          const MAX_WIDTH = 500;
          const MAX_HEIGHT = 500;
          let width = img.width;
          let height = img.height;
  
          if (width > MAX_WIDTH || height > MAX_HEIGHT) {
            if (width > height) {
              height = Math.round((height * MAX_WIDTH) / width);
              width = MAX_WIDTH;
            } else {
              width = Math.round((width * MAX_HEIGHT) / height);
              height = MAX_HEIGHT;
            }
          }
  
          const canvas = document.createElement("canvas");
          canvas.width = width;
          canvas.height = height;
          const ctx = canvas.getContext("2d");
          ctx.drawImage(img, 0, 0, width, height);
  
          imageBase64 = canvas.toDataURL(file.type);
        };
      };
      reader.readAsDataURL(file);
      high();
    }
  
    let textColor = "#000000"; // Default color
    let backgroundColor1 = "#5fe662"; // Default background color 1
    let backgroundColor2 = "#ffffff"; // Default background color 2
    let easing = "ease"; // Default easing
    let code;
    $: code = `a {
        font-size: 4em;
        font-weight: bold;
        font-family: sans-serif;
        color: transparent;
        background: url(${imageBase64}) repeat rgb(19, 17, 17);
        background-position-x: 0%;
        background-position-y: 0%;
        background-size: auto;
        background-clip: border-box;
        background-size: 100%;
        background-position: 0%;
        background-clip: text;
        background-clip: text;
        animation: stripeAnimation 3s linear infinite;
        animation: stripeAnimation 3s linear infinite;
        animation-play-state: running;
        animation-play-state: paused;
        animation-play-state: paused;
      }
    
      a:hover {
        animation-play-state: running;
        animation-play-state: running;
      }
    
      @keyframes stripeAnimation {
        100% {
          background-position-y: -200%;
        }
      }`;
    let htmlcode = `<a href="#"></a>`;
  
    // Copy CSS code to clipboard
    function copyToClipboardCss() {
      navigator.clipboard
        .writeText(code)
        .then(() => {
          toast.success("Copied to clipboard!");
        })
        .catch((err) => {
          toast.error("Failed to copy!");
          console.error(err);
        });
    }
  
    // Copy HTML code to clipboard
    function copyToClipboardHtml() {
      navigator.clipboard
        .writeText(htmlcode)
        .then(() => {
          toast.success("Copied to clipboard!");
        })
        .catch((err) => {
          toast.error("Failed to copy!");
          console.error(err);
        });
    }
  
    function high() {
      const block = document.querySelector(".css code");
      const htmlblock = document.querySelector("pre code");
      if (block) {
        block.classList.remove("hljs"); // Remove the highlight class
        block.removeAttribute("data-highlighted"); // Unset the highlighted attribute
        block.innerHTML = code; // Update the code block content
        hljs.highlightElement(block); // Reapply the highlighting
        block.dataset.highlighted = true; // Set the highlighted attribute again
      }
      hljs.highlightElement(htmlblock);
    }
  
    onMount(() => {
      high();
    });
  </script>
  
  <h1 class="text-gray-400">Upload PNG</h1>
  
  <input type="file" accept="image/png" on:change={handleFileUpload} />
  
  <div>
    <div class="flex justify-center mb-5">
      <!-- svelte-ignore a11y-invalid-attribute -->
      <a href="#" style="--bgimg: {bgimage}" class="border-2 border-gray-500 p-2">
        HELLO!
      </a>
    </div>
  </div>
  
  <!-- HTML part -->
  <h2 class="font-bold text-white text-3xl">html code:</h2>
  <pre>
      <code>{htmlcode}</code>
    </pre>
  <button
    class="btn text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800"
    on:click={copyToClipboardHtml}
  >
    Copy html
  </button>
  
  <!-- CSS part -->
  <h2 class="font-bold text-white text-3xl">css code:</h2>
  <pre class="css">
      <code>{code}</code>
    </pre>
  <button
    class="btn text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800"
    on:click={copyToClipboardCss}
  >
    Copy css
  </button>
  <Toaster />
  
  <style>
    a {
      font-size: 4em;
      font-weight: bold;
      font-family: sans-serif;
      color: transparent;
      background: var(--bgimg) repeat rgb(19, 17, 17);
      background-position-x: 0%;
      background-position-y: 0%;
      background-size: auto;
      background-clip: border-box;
      background-size: 100%;
      background-position: 0%;
      background-clip: text;
      background-clip: text;
      animation: stripeAnimation 3s linear infinite;
      animation: stripeAnimation 3s linear infinite;
      animation-play-state: running;
      animation-play-state: paused;
      animation-play-state: paused;
    }
  
    a:hover {
      animation-play-state: running;
      animation-play-state: running;
    }
  
    @keyframes stripeAnimation {
      100% {
        background-position-y: -200%;
      }
    }
  </style>